// Nettoyage et pr√©paration des donn√©es
const situation = $input.first().json.body.situation;
const rituel = $input.first().json.body.rituel || 'Papier tenu en balance, peseur invoqu√©';

// G√©n√©rer un ID de session unique
const sessionId = `peseur_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

// Nettoyer le texte
const cleanedSituation = situation
  .replace(/[<>"']/g, '') // Supprimer HTML/caract√®res dangereux
  .substring(0, 1800) // Limiter la longueur
  .trim();

// D√©tecter les mots-cl√©s de choix/dilemme
const choiceKeywords = ['ou', 'soit', 'entre', 'choisir', 'd√©cider', 'option', 'alternative', 'dilemme'];
const hasChoiceIndicators = choiceKeywords.some(word => cleanedSituation.toLowerCase().includes(word));

// Analyser l'urgence per√ßue
const urgencyWords = ['urgent', 'rapidement', 'vite', 'imm√©diatement', 'maintenant', 'tout de suite'];
const isUrgent = urgencyWords.some(word => cleanedSituation.toLowerCase().includes(word));

// D√©tecter le moment de la journ√©e
const hour = new Date().getHours();
const timeOfDay = hour < 6 ? 'nuit profonde' : 
                 hour < 12 ? 'matin' : 
                 hour < 18 ? 'apr√®s-midi' : 
                 hour < 22 ? 'soir√©e' : 'nuit';

return {
  cleanedSituation,
  rituel,
  sessionId,
  hasChoiceIndicators,
  isUrgent,
  timeOfDay,
  originalLength: situation.length,
  timestamp: new Date().toISOString()
};

