// Nettoyage et pr√©paration des donn√©es
const situation = $input.first().json.body.situation;
const rituel = $input.first().json.body.rituel || 'Assis confortablement, mains sur ventre, vide accueilli';

// G√©n√©rer un ID de session unique
const sessionId = `habitant_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

// Nettoyer le texte
const cleanedSituation = situation
  .replace(/[<>"']/g, '') // Supprimer HTML/caract√®res dangereux
  .substring(0, 1800) // Limiter la longueur
  .trim();

// D√©tecter les mots-cl√©s de vide/manque
const voidKeywords = ['vide', 'manque', 'absence', 'seul', 'solitude', 'rien', 'creux', 'vide', 'n√©ant', 'isol√©'];
const hasVoidIndicators = voidKeywords.some(word => cleanedSituation.toLowerCase().includes(word));

// Analyser le type de vide
const emotionalVoidKeywords = ['c≈ìur', 'amour', 'affection', 'tendresse', 'emotion', 'sentiment'];
const purposeVoidKeywords = ['sens', 'but', 'objectif', 'direction', 'meaning', 'purpose'];
const socialVoidKeywords = ['ami', 'famille', 'social', 'entourage', 'relation', 'lien'];
const creativeVoidKeywords = ['cr√©er', 'cr√©ativit√©', 'inspiration', 'passion', 'projet'];

const voidType = 
  emotionalVoidKeywords.some(word => cleanedSituation.toLowerCase().includes(word)) ? '√©motionnel' :
  purposeVoidKeywords.some(word => cleanedSituation.toLowerCase().includes(word)) ? 'sens' :
  socialVoidKeywords.some(word => cleanedSituation.toLowerCase().includes(word)) ? 'social' :
  creativeVoidKeywords.some(word => cleanedSituation.toLowerCase().includes(word)) ? 'cr√©atif' :
  'existentiel';

// D√©tecter la profondeur du vide
const depthWords = ['profond', 'abyssal', 'immense', 'total', 'complet', 'absolu'];
const isDeepVoid = depthWords.some(word => cleanedSituation.toLowerCase().includes(word));

// D√©tecter les signes de renouveau
const renewalWords = ['nouveau', 'changement', 'transition', 'd√©but', 'commencer', 'renouveau'];
const hasRenewalSigns = renewalWords.some(word => cleanedSituation.toLowerCase().includes(word));

// D√©tecter le moment de la journ√©e
const hour = new Date().getHours();
const timeOfDay = hour < 6 ? 'nuit profonde' : 
                 hour < 12 ? 'matin' : 
                 hour < 18 ? 'apr√®s-midi' : 
                 hour < 22 ? 'soir√©e' : 'nuit';

return {
  cleanedSituation,
  rituel,
  sessionId,
  hasVoidIndicators,
  voidType,
  isDeepVoid,
  hasRenewalSigns,
  timeOfDay,
  originalLength: situation.length,
  timestamp: new Date().toISOString()
};

